import React, { useState } from 'react';
import { SignIn, UserButton, useAuth } from '@clerk/clerk-react';
import RegistrationModal from './RegistrationModal';

function App() {
  const { isSignedIn, isLoaded } = useAuth();
  const [showModal, setShowModal] = useState(false);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-black text-white px-4">
      <div className="absolute top-4 right-4">
        {isLoaded && isSignedIn && <UserButton />}
      </div>
      <div className="w-36 h-36 mb-6 flex items-center justify-center rounded-full shadow-lg border-4 border-pumpkin bg-black">
        <img
          src={process.env.PUBLIC_URL + '/salem_flag_football_logo.png'}
          alt="Salem Flag Football League Logo"
          className="w-28 h-28 object-contain rounded-full"
        />
      </div>
      <h1 className="text-3xl sm:text-4xl font-extrabold mb-4 text-center tracking-tight text-pumpkin drop-shadow-lg">
        üèà Salem Flag Football League
      </h1>
      {!isSignedIn && isLoaded && (
        <div className="w-full max-w-xs bg-gray-900 rounded-xl shadow-lg p-6 flex flex-col items-center border-2 border-pumpkin">
          <SignIn appearance={{ variables: { colorPrimary: '#FF7518', colorBackground: '#18181b', colorText: '#fff' } }} routing="hash" signUpUrl="/sign-up" />
          <button className="mt-6 w-full py-2 rounded bg-pumpkin text-black font-bold hover:bg-deeporange transition-colors">Learn More</button>
        </div>
      )}
      {isSignedIn && isLoaded && (
        <>
          <p className="text-base sm:text-lg text-pumpkin mb-8 text-center max-w-md font-semibold">
            Welcome! This is the new home for registration, scheduling, payments, and more. Enjoy a sleek, modern, and mobile-first experience.
          </p>
          <div className="bg-gray-900 rounded-xl shadow-lg p-6 w-full max-w-xs flex flex-col items-center border-2 border-pumpkin">
            <span className="text-gray-400 text-sm mb-4">Frontend + Backend scaffolded. Ready to build!</span>
            <button
              className="mt-2 w-full py-2 rounded bg-pumpkin text-black font-bold hover:bg-deeporange transition-colors"
              onClick={() => setShowModal(true)}
            >
              Register Now
            </button>
          </div>
        </>
      )}
      <RegistrationModal isOpen={showModal} onClose={() => setShowModal(false)} />
    </div>
  );
}

export default App;
