import React, { useState, useEffect } from 'react';
import { SignIn, UserButton, useAuth, useUser } from '@clerk/clerk-react';
import RegistrationModal from './RegistrationModal';
import ProfileCompletionModal from './ProfileCompletionModal';
import HomePage from './HomePage';

// Mock user profile storage - in real app this would be in a database
const getUserProfile = (userId: string) => {
  const stored = localStorage.getItem(`userProfile_${userId}`);
  return stored ? JSON.parse(stored) : null;
};

const saveUserProfile = (userId: string, profile: any) => {
  localStorage.setItem(`userProfile_${userId}`, JSON.stringify(profile));
};

function App() {
  const { isSignedIn, isLoaded, userId } = useAuth();
  const { user } = useUser();
  const [showRegistrationModal, setShowRegistrationModal] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [userProfile, setUserProfile] = useState<any>(null);
  const [isProfileComplete, setIsProfileComplete] = useState(false);

  // Check if user has completed their profile
  useEffect(() => {
    if (isSignedIn && isLoaded && userId) {
      const profile = getUserProfile(userId);
      if (profile) {
        setUserProfile(profile);
        setIsProfileComplete(true);
      } else {
        setShowProfileModal(true);
      }
    }
  }, [isSignedIn, isLoaded, userId]);

  const handleProfileComplete = (profileData: any) => {
    if (userId) {
      saveUserProfile(userId, profileData);
      setUserProfile(profileData);
      setIsProfileComplete(true);
      setShowProfileModal(false);
    }
  };

  // Show loading state
  if (!isLoaded) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black">
        <div className="text-pumpkin text-xl">Loading...</div>
      </div>
    );
  }

  // Show sign-in page for unauthenticated users
  if (!isSignedIn) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-black text-white px-4">
        <div className="w-36 h-36 mb-6 flex items-center justify-center rounded-full shadow-lg border-4 border-pumpkin bg-black">
          <img
            src={process.env.PUBLIC_URL + '/salem_flag_football_logo.png'}
            alt="Salem Flag Football League Logo"
            className="w-28 h-28 object-contain rounded-full"
          />
        </div>
        <h1 className="text-3xl sm:text-4xl font-extrabold mb-4 text-center tracking-tight text-pumpkin drop-shadow-lg">
          üèà Salem Flag Football League
        </h1>
        <div className="w-full max-w-xs bg-gray-900 rounded-xl shadow-lg p-6 flex flex-col items-center border-2 border-pumpkin">
          <SignIn appearance={{ variables: { colorPrimary: '#FF7518', colorBackground: '#18181b', colorText: '#fff' } }} routing="hash" signUpUrl="/sign-up" />
          <button className="mt-6 w-full py-2 rounded bg-pumpkin text-black font-bold hover:bg-deeporange transition-colors">Learn More</button>
        </div>
      </div>
    );
  }

  // Show profile completion modal for authenticated users without complete profiles
  if (isSignedIn && !isProfileComplete) {
    return (
      <div className="min-h-screen bg-black">
        <div className="absolute top-4 right-4">
          <UserButton />
        </div>
        <ProfileCompletionModal 
          isOpen={showProfileModal} 
          onComplete={handleProfileComplete}
          clerkUser={user}
        />
      </div>
    );
  }

  // Show homepage for authenticated users with complete profiles
  return (
    <div className="min-h-screen bg-black">
      <HomePage userProfile={userProfile} />
      <RegistrationModal 
        isOpen={showRegistrationModal} 
        onClose={() => setShowRegistrationModal(false)} 
      />
    </div>
  );
}

export default App;
