import React from 'react';
import { SignIn, UserButton, useAuth } from '@clerk/clerk-react';

function App() {
  const { isSignedIn, isLoaded } = useAuth();

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-gray-900 to-gray-800 dark:from-black dark:to-gray-900 text-white px-4">
      <div className="absolute top-4 right-4">
        {isLoaded && isSignedIn && <UserButton />}
      </div>
      <img
        src={process.env.PUBLIC_URL + '/salem_flag_football_logo.png'}
        alt="Salem Flag Football League Logo"
        className="w-32 h-32 mb-6 rounded-full shadow-lg border-4 border-gray-700 object-contain bg-white dark:bg-gray-900"
      />
      <h1 className="text-3xl sm:text-4xl font-extrabold mb-4 text-center tracking-tight">üèà Salem Flag Football League</h1>
      {!isSignedIn && isLoaded && (
        <div className="w-full max-w-xs bg-gray-800 dark:bg-gray-950 rounded-xl shadow-lg p-6 flex flex-col items-center">
          <SignIn appearance={{ variables: { colorPrimary: '#22d3ee', colorBackground: '#18181b', colorText: '#fff' } }} routing="hash" signUpUrl="/sign-up" />
        </div>
      )}
      {isSignedIn && isLoaded && (
        <>
          <p className="text-base sm:text-lg text-gray-300 mb-8 text-center max-w-md">
            Welcome! This is the new home for registration, scheduling, payments, and more. Enjoy a sleek, modern, and mobile-first experience.
          </p>
          <div className="bg-gray-800 dark:bg-gray-950 rounded-xl shadow-lg p-6 w-full max-w-xs flex flex-col items-center">
            <span className="text-gray-400 text-sm">Frontend + Backend scaffolded. Ready to build!</span>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
