import React, { useState } from 'react';

interface RegistrationModalProps {
  isOpen: boolean;
  onClose: () => void;
}

type RegistrationType = 'solo' | 'team';

const MAX_PLAYERS = 7;
const MIN_PLAYERS = 2;

function getInitialTeam() {
  return Array(MIN_PLAYERS).fill({ 
    firstName: '', 
    lastName: '', 
    email: '', 
    phone: '', 
    dateOfBirth: '', 
    gender: '',
    termsAccepted: false,
    communicationsAccepted: false
  });
}

// Email validation with typo check
const COMMON_EMAIL_TYPOS = [
  'gmal.com', 'gmial.com', 'gmaill.com', 'gmail.con', 'gmail.co', 'gmail.cmo',
  'yaho.com', 'yahoo.con', 'yahoo.cmo', 'hotmial.com', 'hotmil.com', 'hotmail.con', 'hotmail.cmo',
  'outlok.com', 'outlook.con', 'outlook.cmo', 'icloud.con', 'icloud.cmo',
  'google.con', 'google.cmo',
];

function isValidEmail(email: string): string | null {
  // Basic email regex
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/;
  if (!emailRegex.test(email)) return 'Invalid email format.';
  const domain = email.split('@')[1]?.toLowerCase() || '';
  for (const typo of COMMON_EMAIL_TYPOS) {
    if (domain.endsWith(typo)) {
      return `Did you mean ${domain.replace(typo, typo.replace('con', 'com'))}?`;
    }
  }
  return null;
}

// US phone validation (accepts (xxx) xxx-xxxx, xxx-xxx-xxxx, xxxxxxxxxx, +1xxxxxxxxxx)
function isValidUSPhone(phone: string): string | null {
  // Remove non-digits
  const digits = phone.replace(/\D/g, '');
  if (digits.length === 11 && digits.startsWith('1')) {
    // Remove leading 1
    phone = digits.slice(1);
  } else if (digits.length === 10) {
    phone = digits;
  } else {
    return 'Phone must be a valid US number (10 digits).';
  }
  // Area code cannot start with 0 or 1
  if (/^[01]/.test(phone)) return 'Area code cannot start with 0 or 1.';
  return null;
}

// Date of birth validation (must be 18+ years old)
function isValidDateOfBirth(dateOfBirth: string): string | null {
  if (!dateOfBirth) return 'Date of birth is required.';
  const birthDate = new Date(dateOfBirth);
  const today = new Date();
  let age = today.getFullYear() - birthDate.getFullYear();
  const monthDiff = today.getMonth() - birthDate.getMonth();
  if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
    age--;
  }
  if (age < 18) return 'You must be at least 18 years old to register.';
  if (age > 100) return 'Please enter a valid date of birth.';
  return null;
}

export default function RegistrationModal({ isOpen, onClose }: RegistrationModalProps) {
  const [type, setType] = useState<RegistrationType>('solo');
  const [solo, setSolo] = useState({ 
    firstName: '', 
    lastName: '', 
    email: '', 
    phone: '', 
    dateOfBirth: '', 
    gender: '',
    termsAccepted: false,
    communicationsAccepted: false
  });
  const [teamName, setTeamName] = useState('');
  const [team, setTeam] = useState(getInitialTeam());
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [fieldErrors, setFieldErrors] = useState<{ [key: string]: string }>({});

  if (!isOpen) return null;

  const handleInput = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>, idx?: number) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    setFieldErrors({});
    if (typeof idx === 'number') {
      const updated = [...team];
      updated[idx] = { 
        ...updated[idx], 
        [name]: type === 'checkbox' ? checked : value 
      };
      setTeam(updated);
    } else {
      setSolo(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : value
      }));
    }
  };

  const handleAddPlayer = () => {
    if (team.length < MAX_PLAYERS) {
      setTeam([...team, { 
        firstName: '', 
        lastName: '', 
        email: '', 
        phone: '', 
        dateOfBirth: '', 
        gender: '',
        termsAccepted: false,
        communicationsAccepted: false
      }]);
    }
  };

  const handleRemovePlayer = (idx: number) => {
    if (team.length > MIN_PLAYERS) {
      setTeam(team.filter((_, i) => i !== idx));
    }
  };

  const validateSolo = () => {
    const errors: { [key: string]: string } = {};
    if (!solo.firstName) errors.firstName = 'First name is required.';
    if (!solo.lastName) errors.lastName = 'Last name is required.';
    const emailError = isValidEmail(solo.email);
    if (!solo.email) errors.email = 'Email is required.';
    else if (emailError) errors.email = emailError;
    const phoneError = isValidUSPhone(solo.phone);
    if (!solo.phone) errors.phone = 'Phone is required.';
    else if (phoneError) errors.phone = phoneError;
    const dobError = isValidDateOfBirth(solo.dateOfBirth);
    if (!solo.dateOfBirth) errors.dateOfBirth = 'Date of birth is required.';
    else if (dobError) errors.dateOfBirth = dobError;
    if (!solo.gender) errors.gender = 'Gender is required.';
    if (!solo.termsAccepted) errors.termsAccepted = 'You must accept the terms of service.';
    return errors;
  };

  const validateTeam = () => {
    const errors: { [key: string]: string } = {};
    if (!teamName) errors.teamName = 'Team name is required.';
    if (team.length < MIN_PLAYERS) errors.team = 'A team must have at least 2 players.';
    team.forEach((player, idx) => {
      if (!player.firstName) errors[`player${idx}_firstName`] = 'First name required.';
      if (!player.lastName) errors[`player${idx}_lastName`] = 'Last name required.';
      const emailError = isValidEmail(player.email);
      if (!player.email) errors[`player${idx}_email`] = 'Email required.';
      else if (emailError) errors[`player${idx}_email`] = emailError;
      const phoneError = isValidUSPhone(player.phone);
      if (!player.phone) errors[`player${idx}_phone`] = 'Phone required.';
      else if (phoneError) errors[`player${idx}_phone`] = phoneError;
      const dobError = isValidDateOfBirth(player.dateOfBirth);
      if (!player.dateOfBirth) errors[`player${idx}_dateOfBirth`] = 'Date of birth required.';
      else if (dobError) errors[`player${idx}_dateOfBirth`] = dobError;
      if (!player.gender) errors[`player${idx}_gender`] = 'Gender required.';
      if (!player.termsAccepted) errors[`player${idx}_termsAccepted`] = 'Terms of service must be accepted.';
    });
    return errors;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setFieldErrors({});
    if (type === 'solo') {
      const errors = validateSolo();
      if (Object.keys(errors).length > 0) {
        setFieldErrors(errors);
        setError('Please fix the errors above.');
        return;
      }
      setSuccess('Solo registration submitted!');
      // TODO: Send to backend
    } else {
      const errors = validateTeam();
      if (Object.keys(errors).length > 0) {
        setFieldErrors(errors);
        setError('Please fix the errors above.');
        return;
      }
      setSuccess('Team registration submitted!');
      // TODO: Send to backend
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-80">
      <div className="bg-gray-900 border-2 border-pumpkin rounded-xl shadow-xl p-6 w-full max-w-md max-h-[90vh] overflow-y-auto relative">
        <button
          className="absolute top-2 right-2 text-pumpkin hover:text-deeporange text-2xl font-bold"
          onClick={onClose}
          aria-label="Close registration modal"
        >
          ×
        </button>
        <h2 className="text-2xl font-bold text-pumpkin mb-4 text-center">Register</h2>
        <div className="flex justify-center mb-4 gap-2">
          <button
            className={`px-4 py-2 rounded font-bold border-2 ${type === 'solo' ? 'bg-pumpkin text-black border-pumpkin' : 'bg-black text-pumpkin border-pumpkin'} transition-colors`}
            onClick={() => { setType('solo'); setError(''); setSuccess(''); setFieldErrors({}); }}
          >
            Solo
          </button>
          <button
            className={`px-4 py-2 rounded font-bold border-2 ${type === 'team' ? 'bg-pumpkin text-black border-pumpkin' : 'bg-black text-pumpkin border-pumpkin'} transition-colors`}
            onClick={() => { setType('team'); setError(''); setSuccess(''); setTeam(getInitialTeam()); setFieldErrors({}); }}
          >
            Team
          </button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          {type === 'solo' ? (
            <>
              <div className="flex gap-2">
                <input
                  className={`flex-1 p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.firstName ? 'border-red-500' : ''}`}
                  name="firstName"
                  placeholder="First Name"
                  value={solo.firstName}
                  onChange={handleInput}
                  autoComplete="given-name"
                />
                <input
                  className={`flex-1 p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.lastName ? 'border-red-500' : ''}`}
                  name="lastName"
                  placeholder="Last Name"
                  value={solo.lastName}
                  onChange={handleInput}
                  autoComplete="family-name"
                />
              </div>
              {fieldErrors.firstName && <div className="text-red-400 text-xs mb-1">{fieldErrors.firstName}</div>}
              {fieldErrors.lastName && <div className="text-red-400 text-xs mb-1">{fieldErrors.lastName}</div>}
              <input
                className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.email ? 'border-red-500' : ''}`}
                name="email"
                placeholder="Email"
                value={solo.email}
                onChange={handleInput}
                autoComplete="email"
                type="email"
              />
              {fieldErrors.email && <div className="text-red-400 text-xs mb-1">{fieldErrors.email}</div>}
              <input
                className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.phone ? 'border-red-500' : ''}`}
                name="phone"
                placeholder="Phone"
                value={solo.phone}
                onChange={handleInput}
                autoComplete="tel"
                type="tel"
              />
              {fieldErrors.phone && <div className="text-red-400 text-xs mb-1">{fieldErrors.phone}</div>}
              <input
                className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.dateOfBirth ? 'border-red-500' : ''}`}
                name="dateOfBirth"
                placeholder="Date of Birth"
                value={solo.dateOfBirth}
                onChange={handleInput}
                autoComplete="bday"
                type="date"
              />
              {fieldErrors.dateOfBirth && <div className="text-red-400 text-xs mb-1">{fieldErrors.dateOfBirth}</div>}
              <select
                className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.gender ? 'border-red-500' : ''}`}
                name="gender"
                value={solo.gender}
                onChange={handleInput}
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
                <option value="prefer-not-to-say">Prefer not to say</option>
              </select>
              {fieldErrors.gender && <div className="text-red-400 text-xs mb-1">{fieldErrors.gender}</div>}

              <div className="space-y-3 pt-2">
                <label className="flex items-start gap-3 cursor-pointer">
                  <input
                    type="checkbox"
                    name="termsAccepted"
                    checked={solo.termsAccepted}
                    onChange={handleInput}
                    className="mt-1 w-4 h-4 text-pumpkin bg-black border-pumpkin rounded focus:ring-pumpkin focus:ring-2"
                  />
                  <span className="text-sm text-gray-300">
                    I agree to the <a href="/terms" className="text-pumpkin hover:text-deeporange underline">Terms of Service</a> and acknowledge that by registering, I am bound by these terms.
                  </span>
                </label>
                {fieldErrors.termsAccepted && <div className="text-red-400 text-xs">{fieldErrors.termsAccepted}</div>}

                <label className="flex items-start gap-3 cursor-pointer">
                  <input
                    type="checkbox"
                    name="communicationsAccepted"
                    checked={solo.communicationsAccepted}
                    onChange={handleInput}
                    className="mt-1 w-4 h-4 text-pumpkin bg-black border-pumpkin rounded focus:ring-pumpkin focus:ring-2"
                  />
                  <span className="text-sm text-gray-300">
                    I consent to receive communications about league activities, schedules, and updates via email.
                  </span>
                </label>
              </div>
            </>
          ) : (
            <>
              <input
                className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors.teamName ? 'border-red-500' : ''}`}
                name="teamName"
                placeholder="Team Name"
                value={teamName}
                onChange={e => setTeamName(e.target.value)}
                autoComplete="off"
              />
              {fieldErrors.teamName && <div className="text-red-400 text-xs mb-1">{fieldErrors.teamName}</div>}
              {team.map((player, idx) => (
                <div key={idx} className="flex flex-col gap-2 mb-2 w-full">
                  <div className="flex gap-2">
                    <input
                      className={`flex-1 p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin min-w-0 ${fieldErrors[`player${idx}_firstName`] ? 'border-red-500' : ''}`}
                      name="firstName"
                      placeholder={`Player ${idx + 1} First Name`}
                      value={player.firstName}
                      onChange={e => handleInput(e, idx)}
                      autoComplete="off"
                    />
                    <input
                      className={`flex-1 p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin min-w-0 ${fieldErrors[`player${idx}_lastName`] ? 'border-red-500' : ''}`}
                      name="lastName"
                      placeholder="Last Name"
                      value={player.lastName}
                      onChange={e => handleInput(e, idx)}
                      autoComplete="off"
                    />
                  </div>
                  {fieldErrors[`player${idx}_firstName`] && <div className="text-red-400 text-xs mb-1">{fieldErrors[`player${idx}_firstName`]}</div>}
                  {fieldErrors[`player${idx}_lastName`] && <div className="text-red-400 text-xs mb-1">{fieldErrors[`player${idx}_lastName`]}</div>}
                  <input
                    className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors[`player${idx}_email`] ? 'border-red-500' : ''}`}
                    name="email"
                    placeholder="Email"
                    value={player.email}
                    onChange={e => handleInput(e, idx)}
                    autoComplete="off"
                    type="email"
                  />
                  {fieldErrors[`player${idx}_email`] && <div className="text-red-400 text-xs mb-1">{fieldErrors[`player${idx}_email`]}</div>}
                  <input
                    className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors[`player${idx}_phone`] ? 'border-red-500' : ''}`}
                    name="phone"
                    placeholder="Phone"
                    value={player.phone}
                    onChange={e => handleInput(e, idx)}
                    autoComplete="off"
                    type="tel"
                  />
                  {fieldErrors[`player${idx}_phone`] && <div className="text-red-400 text-xs mb-1">{fieldErrors[`player${idx}_phone`]}</div>}
                  <input
                    className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors[`player${idx}_dateOfBirth`] ? 'border-red-500' : ''}`}
                    name="dateOfBirth"
                    placeholder="Date of Birth"
                    value={player.dateOfBirth}
                    onChange={e => handleInput(e, idx)}
                    autoComplete="off"
                    type="date"
                  />
                  {fieldErrors[`player${idx}_dateOfBirth`] && <div className="text-red-400 text-xs mb-1">{fieldErrors[`player${idx}_dateOfBirth`]}</div>}
                  <select
                    className={`w-full p-2 rounded bg-black border border-pumpkin text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-pumpkin ${fieldErrors[`player${idx}_gender`] ? 'border-red-500' : ''}`}
                    name="gender"
                    value={player.gender}
                    onChange={e => handleInput(e, idx)}
                  >
                    <option value="">Select Gender</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="other">Other</option>
                    <option value="prefer-not-to-say">Prefer not to say</option>
                  </select>
                  {fieldErrors[`player${idx}_gender`] && <div className="text-red-400 text-xs mb-1">{fieldErrors[`player${idx}_gender`]}</div>}

                  <div className="space-y-3 pt-2">
                    <label className="flex items-start gap-3 cursor-pointer">
                      <input
                        type="checkbox"
                        name="termsAccepted"
                        checked={player.termsAccepted}
                        onChange={e => handleInput(e, idx)}
                        className="mt-1 w-4 h-4 text-pumpkin bg-black border-pumpkin rounded focus:ring-pumpkin focus:ring-2"
                      />
                      <span className="text-sm text-gray-300">
                        I agree to the <a href="/terms" className="text-pumpkin hover:text-deeporange underline">Terms of Service</a> and acknowledge that by registering, I am bound by these terms.
                      </span>
                    </label>
                    {fieldErrors[`player${idx}_termsAccepted`] && <div className="text-red-400 text-xs">{fieldErrors[`player${idx}_termsAccepted`]}</div>}

                    <label className="flex items-start gap-3 cursor-pointer">
                      <input
                        type="checkbox"
                        name="communicationsAccepted"
                        checked={player.communicationsAccepted}
                        onChange={e => handleInput(e, idx)}
                        className="mt-1 w-4 h-4 text-pumpkin bg-black border-pumpkin rounded focus:ring-pumpkin focus:ring-2"
                      />
                      <span className="text-sm text-gray-300">
                        I consent to receive communications about league activities, schedules, and updates via email.
                      </span>
                    </label>
                  </div>

                  {team.length > MIN_PLAYERS && (
                    <button
                      type="button"
                      className="w-full py-1 rounded bg-deeporange text-white font-bold hover:bg-pumpkin transition-colors"
                      onClick={() => handleRemovePlayer(idx)}
                      aria-label={`Remove player ${idx + 1}`}
                    >
                      Remove Player
                    </button>
                  )}
                </div>
              ))}
              {team.length < MAX_PLAYERS && (
                <button
                  type="button"
                  className="w-full py-2 rounded bg-pumpkin text-black font-bold hover:bg-deeporange transition-colors mb-2"
                  onClick={handleAddPlayer}
                >
                  + Add Player
                </button>
              )}
            </>
          )}
          {error && <div className="text-red-400 text-sm text-center">{error}</div>}
          {success && <div className="text-green-400 text-sm text-center">{success}</div>}
          <button
            type="submit"
            className="w-full py-2 rounded bg-pumpkin text-black font-bold hover:bg-deeporange transition-colors mt-2"
          >
            Submit Registration
          </button>
        </form>
      </div>
    </div>
  );
}
