import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { UserButton, useAuth, useUser } from '@clerk/clerk-react';
import ProfileCompletionModal from './ProfileCompletionModal';
import PublicHomePage from './PublicHomePage';
import ProfilePage from './ProfilePage';

// Mock user profile storage - in real app this would be in a database
const getUserProfile = (userId: string) => {
  const stored = localStorage.getItem(`userProfile_${userId}`);
  return stored ? JSON.parse(stored) : null;
};

const saveUserProfile = (userId: string, profile: any) => {
  localStorage.setItem(`userProfile_${userId}`, JSON.stringify(profile));
};

function App() {
  const { isSignedIn, isLoaded, userId } = useAuth();
  const { user } = useUser();
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [isProfileComplete, setIsProfileComplete] = useState(false);

  // Check if user has completed their profile
  useEffect(() => {
    if (isSignedIn && isLoaded && userId) {
      const profile = getUserProfile(userId);
      if (profile) {
        setIsProfileComplete(true);
      } else {
        setShowProfileModal(true);
      }
    }
  }, [isSignedIn, isLoaded, userId]);

  const handleProfileComplete = (profileData: any) => {
    if (userId) {
      saveUserProfile(userId, profileData);
      setIsProfileComplete(true);
      setShowProfileModal(false);
    }
  };

  // Show loading state
  if (!isLoaded) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-black">
        <div className="text-pumpkin text-xl">Loading...</div>
      </div>
    );
  }

  return (
    <Router>
      <div className="min-h-screen bg-black">
        {/* Show UserButton for authenticated users */}
        {isSignedIn && (
          <div className="absolute top-4 right-4 z-10">
            <UserButton />
          </div>
        )}

        {/* Routes */}
        <Routes>
          <Route path="/" element={<PublicHomePage />} />
          <Route path="/profile" element={<ProfilePage />} />
        </Routes>

        {/* Show profile completion modal for authenticated users without complete profiles */}
        {isSignedIn && !isProfileComplete && (
          <ProfileCompletionModal 
            isOpen={showProfileModal} 
            onComplete={handleProfileComplete}
            clerkUser={user}
          />
        )}
      </div>
    </Router>
  );
}

export default App;
