import React, { useState, useEffect } from 'react';
import { useUser } from '@clerk/clerk-react';
import { Link } from 'react-router-dom';

interface UserProfile {
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  dateOfBirth: string;
  gender: string;
  communicationsAccepted: boolean;
  registrationStatus: 'registered' | 'pending' | 'not_registered';
  teamName?: string;
  groupName?: string;
  registrationDate?: string;
}

export default function ProfilePage() {
  const { user } = useUser();
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Mock profile data - in real app this would come from API
  useEffect(() => {
    if (user) {
      // Simulate API call
      setTimeout(() => {
        const mockProfile: UserProfile = {
          firstName: user.firstName || '',
          lastName: user.lastName || '',
          email: user.primaryEmailAddress?.emailAddress || '',
          phone: '(555) 123-4567',
          dateOfBirth: '1990-01-15',
          gender: 'male',
          communicationsAccepted: true,
          registrationStatus: 'registered',
          teamName: 'Pumpkin Patriots',
          registrationDate: '2024-01-10'
        };
        setProfile(mockProfile);
        setIsLoading(false);
      }, 500);
    }
  }, [user]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    const checked = (e.target as HTMLInputElement).checked;

    setProfile(prev => prev ? {
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    } : null);
  };

  const handleSave = async () => {
    setIsSaving(true);
    setError('');
    setSuccess('');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      setSuccess('Profile updated successfully!');
      setIsEditing(false);
    } catch (err) {
      setError('Failed to update profile. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const handleCancel = () => {
    // Reset to original data
    if (user) {
      const originalProfile: UserProfile = {
        firstName: user.firstName || '',
        lastName: user.lastName || '',
        email: user.primaryEmailAddress?.emailAddress || '',
        phone: '(555) 123-4567',
        dateOfBirth: '1990-01-15',
        gender: 'male',
        communicationsAccepted: true,
        registrationStatus: 'registered',
        teamName: 'Pumpkin Patriots',
        registrationDate: '2024-01-10'
      };
      setProfile(originalProfile);
    }
    setIsEditing(false);
    setError('');
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-black text-white relative">
        {/* Fixed Background Image */}
        <div 
          className="fixed inset-0 bg-cover bg-center bg-no-repeat"
          style={{
            backgroundImage: `url(${process.env.PUBLIC_URL + '/salem_common.png'})`
          }}
        >
          <div className="absolute inset-0 bg-black bg-opacity-70"></div>
        </div>

        <div className="relative z-10 flex items-center justify-center min-h-screen">
          <div className="text-pumpkin text-xl">Loading profile...</div>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="min-h-screen bg-black text-white relative">
        {/* Fixed Background Image */}
        <div 
          className="fixed inset-0 bg-cover bg-center bg-no-repeat"
          style={{
            backgroundImage: `url(${process.env.PUBLIC_URL + '/salem_common.png'})`
          }}
        >
          <div className="absolute inset-0 bg-black bg-opacity-70"></div>
        </div>

        <div className="relative z-10 flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="text-red-400 text-xl mb-4">Profile not found</div>
            <Link to="/" className="text-pumpkin hover:text-deeporange">
              Return to Home
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white relative">
      {/* Fixed Background Image */}
      <div 
        className="fixed inset-0 bg-cover bg-center bg-no-repeat"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL + '/salem_common.png'})`
        }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-70"></div>
      </div>

      {/* Content Container */}
      <div className="relative z-10">
        {/* Header */}
        <header className="bg-gunmetal bg-opacity-95 border-b-2 border-pumpkin p-4">
          <div className="max-w-6xl mx-auto flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link to="/" className="flex items-center gap-4 hover:opacity-80 transition-opacity">
                <div className="w-12 h-12 flex items-center justify-center rounded-full shadow-lg border-2 border-pumpkin bg-black">
                  <img
                    src={process.env.PUBLIC_URL + '/salem_flag_football_logo.png'}
                    alt="Salem Flag Football League Logo"
                    className="w-8 h-8 object-contain rounded-full"
                  />
                </div>
                <div>
                  <h1 className="text-xl font-bold text-pumpkin">Salem Flag Football League</h1>
                  <p className="text-sm text-gray-400">Your Profile</p>
                </div>
              </Link>
            </div>
            <Link 
              to="/" 
              className="px-4 py-2 rounded border-2 border-pumpkin text-pumpkin font-bold hover:bg-pumpkin hover:text-black transition-colors"
            >
              Back to Home
            </Link>
          </div>
        </header>

        {/* Main Content */}
        <main className="max-w-4xl mx-auto p-4">
          {/* Profile Header */}
          <div className="bg-gunmetal bg-opacity-95 border-2 border-pumpkin rounded-xl p-6 mb-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold text-pumpkin">Profile Information</h2>
              <div className="flex gap-2">
                {!isEditing ? (
                  <button
                    onClick={() => setIsEditing(true)}
                    className="px-4 py-2 rounded bg-pumpkin text-black font-bold hover:bg-deeporange transition-colors"
                  >
                    Edit Profile
                  </button>
                ) : (
                  <>
                    <button
                      onClick={handleCancel}
                      className="px-4 py-2 rounded border-2 border-pumpkin text-pumpkin font-bold hover:bg-pumpkin hover:text-black transition-colors"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={handleSave}
                      disabled={isSaving}
                      className={`px-4 py-2 rounded font-bold transition-colors ${
                        isSaving 
                          ? 'bg-gray-600 text-gray-400 cursor-not-allowed' 
                          : 'bg-pumpkin text-black hover:bg-deeporange'
                      }`}
                    >
                      {isSaving ? 'Saving...' : 'Save Changes'}
                    </button>
                  </>
                )}
              </div>
            </div>

            {error && <div className="text-red-400 text-sm mb-4">{error}</div>}
            {success && <div className="text-green-400 text-sm mb-4">{success}</div>}

            {/* Registration Status */}
            <div className="mb-6 p-4 bg-black bg-opacity-30 rounded-lg">
              <h3 className="text-lg font-bold text-pumpkin mb-2">League Registration Status</h3>
              <div className="flex items-center gap-3">
                <div className={`w-3 h-3 rounded-full ${
                  profile.registrationStatus === 'registered' ? 'bg-green-500' :
                  profile.registrationStatus === 'pending' ? 'bg-yellow-500' : 'bg-red-500'
                }`}></div>
                <span className="font-semibold">
                  {profile.registrationStatus === 'registered' ? '✅ Registered' :
                   profile.registrationStatus === 'pending' ? '⏳ Pending' : '❌ Not Registered'}
                </span>
                {profile.registrationStatus === 'registered' && profile.teamName && (
                  <span className="text-gray-300">• Team: {profile.teamName}</span>
                )}
                {profile.registrationStatus === 'registered' && profile.registrationDate && (
                  <span className="text-gray-300">• Since: {new Date(profile.registrationDate).toLocaleDateString()}</span>
                )}
              </div>
            </div>

            {/* Profile Form */}
            <form className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-1">First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={profile.firstName}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                    className={`w-full p-3 rounded bg-black border border-pumpkin text-white focus:outline-none focus:ring-2 focus:ring-pumpkin ${
                      !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-1">Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={profile.lastName}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                    className={`w-full p-3 rounded bg-black border border-pumpkin text-white focus:outline-none focus:ring-2 focus:ring-pumpkin ${
                      !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">Email Address</label>
                <input
                  type="email"
                  name="email"
                  value={profile.email}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={`w-full p-3 rounded bg-black border border-pumpkin text-white focus:outline-none focus:ring-2 focus:ring-pumpkin ${
                    !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                  }`}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-1">Phone Number</label>
                <input
                  type="tel"
                  name="phone"
                  value={profile.phone}
                  onChange={handleInputChange}
                  disabled={!isEditing}
                  className={`w-full p-3 rounded bg-black border border-pumpkin text-white focus:outline-none focus:ring-2 focus:ring-pumpkin ${
                    !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                  }`}
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-1">Date of Birth</label>
                  <input
                    type="date"
                    name="dateOfBirth"
                    value={profile.dateOfBirth}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                    className={`w-full p-3 rounded bg-black border border-pumpkin text-white focus:outline-none focus:ring-2 focus:ring-pumpkin ${
                      !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-1">Gender</label>
                  <select
                    name="gender"
                    value={profile.gender}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                    className={`w-full p-3 rounded bg-black border border-pumpkin text-white focus:outline-none focus:ring-2 focus:ring-pumpkin ${
                      !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    <option value="">Select Gender</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="other">Other</option>
                    <option value="prefer-not-to-say">Prefer not to say</option>
                  </select>
                </div>
              </div>

              <div className="pt-4">
                <label className="flex items-start gap-3 cursor-pointer">
                  <input
                    type="checkbox"
                    name="communicationsAccepted"
                    checked={profile.communicationsAccepted}
                    onChange={handleInputChange}
                    disabled={!isEditing}
                    className={`mt-1 w-4 h-4 text-pumpkin bg-black border-pumpkin rounded focus:ring-pumpkin focus:ring-2 ${
                      !isEditing ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  />
                  <span className="text-sm text-gray-300">
                    I consent to receive communications about league activities, schedules, and updates via email.
                  </span>
                </label>
              </div>
            </form>
          </div>

          {/* League Information */}
          {profile.registrationStatus === 'registered' && (
            <div className="bg-gunmetal bg-opacity-95 border-2 border-pumpkin rounded-xl p-6">
              <h3 className="text-xl font-bold text-pumpkin mb-4">League Information</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <h4 className="font-semibold text-gray-300 mb-2">Current Team</h4>
                  <p className="text-pumpkin font-bold">{profile.teamName}</p>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-300 mb-2">Registration Date</h4>
                  <p className="text-white">{profile.registrationDate ? new Date(profile.registrationDate).toLocaleDateString() : 'N/A'}</p>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-300 mb-2">Season Status</h4>
                  <p className="text-green-400">Active</p>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-300 mb-2">Next Game</h4>
                  <p className="text-white">Tuesday, 6:00 PM at Salem Common</p>
                </div>
              </div>
            </div>
          )}

          {/* Quick Actions */}
          <div className="bg-gunmetal bg-opacity-95 border-2 border-pumpkin rounded-xl p-6 mt-6">
            <h3 className="text-xl font-bold text-pumpkin mb-4">Quick Actions</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <Link 
                to="/rules" 
                className="p-4 bg-black bg-opacity-30 rounded-lg border border-pumpkin hover:bg-pumpkin hover:text-black transition-colors text-center"
              >
                <div className="text-2xl mb-2">📋</div>
                <div className="font-semibold">View Rules</div>
              </Link>
              <Link 
                to="/" 
                className="p-4 bg-black bg-opacity-30 rounded-lg border border-pumpkin hover:bg-pumpkin hover:text-black transition-colors text-center"
              >
                <div className="text-2xl mb-2">📅</div>
                <div className="font-semibold">View Schedule</div>
              </Link>
              <Link 
                to="/contact" 
                className="p-4 bg-black bg-opacity-30 rounded-lg border border-pumpkin hover:bg-pumpkin hover:text-black transition-colors text-center"
              >
                <div className="text-2xl mb-2">📞</div>
                <div className="font-semibold">Contact League</div>
              </Link>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
